-- ----------------------------
-- View structure for books_views
-- ----------------------------
DROP VIEW IF EXISTS "public"."books_views";
CREATE VIEW "public"."books_views" AS  SELECT books."Book_Title",
    books."ISBN",
    authors."Author_Name",
    authors."Country"
   FROM authors
     JOIN book_authors ON authors."Author_ID" = book_authors."Author_FK"
     JOIN books ON books."Book_ID" = book_authors."Book_FK";

-- ----------------------------
-- View structure for contents_view
-- ----------------------------
DROP VIEW IF EXISTS "public"."contents_view";
CREATE VIEW "public"."contents_view" AS  SELECT contents.title,
    contents.url,
    contents.media_id,
    content_type.type_name,
    contents.type_id
   FROM contents
     JOIN content_type ON contents.type_id = content_type.id;

