INSERT INTO "public"."contents" VALUES ('Isodec Supports 200 Needy Students', 1, '4c4b1154-b7ad-4319-a183-8a9ac5ebee52', 'Isodec Supports 200 Needy Students', '<p>A NON-GOVERNMENTAL Organisation (NGO), The Integrated Social Development Centre (ISODEC) with support from Ahafo Ano­North District Assembly (AANDA) is to support 200 brilliant but needy girls in Ahafo Ano-North District&nbsp;</p><p>Heads of first cycle schools and the two senior secondary schools in the district are to select girls who are to benefit from the scholarship scheme.&nbsp;</p><p>This was made known by Mrs Alice L. Asabia, Ashanti Regional Co­ordinator of ISODEC at a durbar organised to round-off activities marking the celebration of Girls Education Week at Tepa.&nbsp;</p><p>Mrs Asabia said since the inception of the scholarship scheme in the district in 1998, 40 girls from SSS, 160 from Junior Secondary Schools and 200 from primary schools have benefited from the scheme.&nbsp;</p><p>The ISODEC co-ordinator noted that even though tracer studies on past beneficiaries of the ISODEC/AANDA scholarship indicate that girls who completed SSS could not further their education, ISODEC is still committed to human resource development with emphasis on the girl-child.&nbsp;</p><p>A report by AANDA on girl-child education in the district showed that the enrolment of girls in first and second cycle schools in the district has slightly increased from 5,968 in the 1999/2000 academic year to 6,011 this academic year.&nbsp;</p><p>The report noted that the girl-child education project and the district directorate of education in their bid to improve girl-child education have been organising vacation classes for girls in the district at their own expense.&nbsp;</p><p>The report also commended the Omanhene of Tepa traditional area, Nana Adusei Atwenewaa Ampem I, for his continuous support for education in the district.&nbsp;</p>', '62385815e1f74.jpg', 'https://www.ghanaweb.com/GhanaHomePage/NewsArchive/artikel.php?ID=19304', '2001-11-05 00:00:00', NULL, 1, NULL, 17);
INSERT INTO "public"."contents" VALUES ('Natural Resource Management', 1, 'c4208c18-c59d-40bd-a0cb-c79d0c0552a8', 'Natural Resource Management', '<h1 class="title" style="margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px 0px 0.5em; line-height: 1.3em; color: rgb(0, 0, 0); font-size: 2.2em; font-family: Palanquin, helvetica, Arial, sans-serif; letter-spacing: 0.32px;">Ghana National Coalition Against the Privatisation (CAP) of Water</h1><p style="margin-right: 0px; margin-bottom: 0px; margin-left: 0px; padding: 0px 0px 0.5em; line-height: 1.3em; color: rgb(0, 0, 0); font-size: 2.2em; font-family: Palanquin, helvetica, Arial, sans-serif; letter-spacing: 0.32px;"><span style="color: rgb(72, 75, 78); font-size: 16px; letter-spacing: 0.32px;">Organised by Integrated Social Development Centre (ISODEC), the Ghana National Coalition Against the Privitisation (CAP) of Water is grounded in the conviction that privatisation of urban water supply is a threat to public accountability, democratisation, and equitable development. The CAP of Water is a broad-based coalition of individuals and civil society organisations (such as trade unions; women/gender rights groups; student and youth groups; tenants, residents, and community associations; religious bodies; and pro-Third World development campaigners) united around the goal of ensuring access to water for all Ghanaians (backed by a statutory right to water) by 2008.</span></p><div class="field field-type-text field-field-long-text-1" style="margin: 0px 0px 13.3333px; padding: 0px; font-size: 0.9em; color: rgb(72, 75, 78); font-family: Palanquin, helvetica, Arial, sans-serif; letter-spacing: 0.32px;"><div class="field-label" style="margin: 0px; padding: 0px; font-weight: bold;">Communication Strategies:&nbsp;</div><div class="field-items" style="margin: 0px; padding: 0px;"><div class="field-item odd" style="margin: 0px; padding: 0px;"><p style="margin-right: 0px; margin-bottom: 1em; margin-left: 0px; padding: 0px; font-size: 0.9em;">This programme involves the following components:</p><ul type="square" style="margin-right: 0px; margin-bottom: 1em; margin-left: 0px; padding: 0px 0px 0px 40px; list-style-position: initial; list-style-image: initial; font-size: 0.9em;"><li style="margin: 0px; padding: 0px;">A mass civil society campaign that works to facilitate public debate; educate citizens through community, school, and work-place teach-ins; and mobilise local coalition communities, promote petitions, and pass resolutions to stop the transfer of water supply to foreign multinational monopoly control.</li><li style="margin: 0px; padding: 0px;">Direct mass involvement in decisions about the alternatives for reforms in the water sector.</li><li style="margin: 0px; padding: 0px;">The inclusion of a public sector option in water supply and the overall restructuring of the water sector. Details of such an option would then be publicised and made available for independent verification.</li><li style="margin: 0px; padding: 0px;">Full public disclosure of all documents and details of transaction proposals, bids, and negotiations involving all involved parties.</li></ul><div class="field field-type-text field-field-long-text-1" style="margin: 0px 0px 13.3333px; padding: 0px; font-size: 0.9em; letter-spacing: 0.32px;"><div class="field-items" style="margin: 0px; padding: 0px;"><div class="field-item odd" style="margin: 0px; padding: 0px;"><p style="margin-right: 0px; margin-bottom: 1em; margin-left: 0px; padding: 0px; font-size: 0.9em;">The CAP of Water is in the process of publishing tens of thousands of information sheets and a newsletter, both of which highlight these issues and reinforce these strategies. Currently under discussion by coalition partners is a proposed national demonstration against water privatisation.</p></div></div></div><div class="field field-type-text field-field-long-text-6" style="margin: 0px 0px 13.3333px; padding: 0px; font-size: 0.9em; letter-spacing: 0.32px;"><div class="field-label" style="margin: 0px; padding: 0px; font-weight: bold;">Development Issues:&nbsp;</div><div class="field-items" style="margin: 0px; padding: 0px;"><div class="field-item odd" style="margin: 0px; padding: 0px;"><p style="margin-right: 0px; margin-bottom: 1em; margin-left: 0px; padding: 0px; font-size: 0.9em;">Health, Sanitation, Rights.</p></div></div></div><div class="field field-type-text field-field-long-text-2" style="margin: 0px 0px 13.3333px; padding: 0px; font-size: 0.9em; letter-spacing: 0.32px;"><div class="field-label" style="margin: 0px; padding: 0px; font-weight: bold;">Key Points:&nbsp;</div><div class="field-items" style="margin: 0px; padding: 0px;"><div class="field-item odd" style="margin: 0px; padding: 0px;"><p style="margin-right: 0px; margin-bottom: 1em; margin-left: 0px; padding: 0px; font-size: 0.9em;">There are now five multinational corporations bidding to lease the Ghana Water Company. Information about the terms and conditions of the privatisation, such as the proposed tariff structure, expansion plans, and possible subsidies, has not been made available to the public. Citizens cannot assess the relative advantages or disadvantages of the privatisation plan unless this information is in the public domain.<br style="margin: 0px; padding: 0px;"><br style="margin: 0px; padding: 0px;"><br style="margin: 0px; padding: 0px;"></p><p style="margin-right: 0px; margin-bottom: 1em; margin-left: 0px; padding: 0px; font-size: 0.9em;">The CAP of Water programme was developed out of a forum in May 2001 that aimed to encourage the government of Ghana to facilitate the decision by the previous government to privatise the delivery of water in the country. The forum, dubbed "Water is for life and every life matters" sought to promote informed public debate on a wide range of water management options. Part one of the event was a public forum inviting all stakeholders (including government officials, donors, other poor water users, and NGOs in the water industry and other social activists) to present their views about water management issues in the context of poverty and public health and to compare these experiences with those from other countries and regions.</p><p style="margin-right: 0px; margin-bottom: 1em; margin-left: 0px; padding: 0px; font-size: 0.9em;">Objectives of this portion of the forum included:</p><ul type="square" style="margin-right: 0px; margin-bottom: 1em; margin-left: 0px; padding: 0px 0px 0px 40px; list-style-position: initial; list-style-image: initial; font-size: 0.9em;"><li style="margin: 0px; padding: 0px;">Exploring the linkages between national policies (e.g the privatisation of public utilities), and the policies of bilateral donors, the international financial institutions, and the World Trade Organization;</li><li style="margin: 0px; padding: 0px;">Sharing practical strategies for engaging policymakers at the national and international levels through effective communication and focused advocacy; and</li><li style="margin: 0px; padding: 0px;">Identifying areas for capacity building and campaign issues and developing organisational structures for follow-up activities.</li></ul><p style="margin-right: 0px; margin-bottom: 1em; margin-left: 0px; padding: 0px; font-size: 0.9em; letter-spacing: 0.32px;">This portion of the forum included drama, songs, and video documentaries.<br style="margin: 0px; padding: 0px;"><br style="margin: 0px; padding: 0px;"><br style="margin: 0px; padding: 0px;"></p><p style="margin-right: 0px; margin-bottom: 1em; margin-left: 0px; padding: 0px; font-size: 0.9em; letter-spacing: 0.32px;">A strategising session, which constituted the second part of the programme, provided the opportunity for poor water users, water and sanitation committees, policy analysts, NGOs, public health activists, and other civil society groups to share experiences, learn new tools for analysis and advocacy, and strategies about ways to foster an equitable water policy. Specifically, part two focused on:</p><ul type="square" style="margin-right: 0px; margin-bottom: 1em; margin-left: 0px; padding: 0px 0px 0px 40px; list-style-position: initial; list-style-image: initial; font-size: 0.9em; letter-spacing: 0.32px;"><li style="margin: 0px; padding: 0px;">The quality and quantity of water</li><li style="margin: 0px; padding: 0px;">Privatisation of water as only a part of the global agenda on privatisation</li><li style="margin: 0px; padding: 0px;">The failure of the public sector to provide affordable and quality water</li><li style="margin: 0px; padding: 0px;">Enhancement of the delivery of water in a way that is affordable and will improve health</li><li style="margin: 0px; padding: 0px;">Sanitation - whose responsibility is it?</li><li style="margin: 0px; padding: 0px;">De-linking rural water from urban water</li><li style="margin: 0px; padding: 0px;">Making communities aware about privatisation issues in order to promote good health.</li></ul><div class="field field-type-text field-field-long-text-2" style="margin: 0px 0px 13.3333px; padding: 0px; font-size: 0.9em; letter-spacing: 0.32px;"><div class="field-items" style="margin: 0px; padding: 0px;"><div class="field-item odd" style="margin: 0px; padding: 0px;"><p style="margin-right: 0px; margin-bottom: 1em; margin-left: 0px; padding: 0px; font-size: 0.9em;">Participants collectively agreed that they were ready to begin a campaign to stop the privatisation of water in Ghana and to explore viable alternatives for the water delivery process. A seven-member management committee was formed to coordinate the activities of this coalition.<br style="margin: 0px; padding: 0px;"><br style="margin: 0px; padding: 0px;"><br style="margin: 0px; padding: 0px;"></p><p style="margin-right: 0px; margin-bottom: 1em; margin-left: 0px; padding: 0px; font-size: 0.9em;">Perhaps in part because of this programme, the Christian Council of Ghana has taken an official public stand against the privatisation of water in Ghana. Many other mass civil society institutions and organisations, at every level of their organisations and memberships -- from local mosques and churches to national trade unions -- are expected to declare their commitments publicly as well.</p></div></div></div><div class="field field-type-text field-field-partner-text" style="margin: 0px 0px 13.3333px; padding: 0px; font-size: 0.9em; letter-spacing: 0.32px;"><div class="field-label" style="margin: 0px; padding: 0px; font-weight: bold;">Partner Text:&nbsp;</div><div class="field-items" style="margin: 0px; padding: 0px;"><div class="field-item odd" style="margin: 0px; padding: 0px;"><p style="margin-right: 0px; margin-bottom: 1em; margin-left: 0px; padding: 0px; font-size: 0.9em;">SAMWU, South Africa, and Globalisation Challenge Initiative, USA.</p></div></div></div><div class="field field-type-text field-field-source-text" style="margin: 0px 0px 13.3333px; padding: 0px; font-size: 0.9em; letter-spacing: 0.32px;"><div class="field-label" style="margin: 0px; padding: 0px; font-weight: bold;">Source:&nbsp;</div><div class="field-items" style="margin: 0px; padding: 0px;"><div class="field-item odd" style="margin: 0px; padding: 0px;"><p style="margin-right: 0px; margin-bottom: 1em; margin-left: 0px; padding: 0px; font-size: 0.9em;"><a href="http://www.isodec.org.gh/isodec/campaigns.htm" target="_blank" class="ext" style="margin: 0px; padding: 0px; color: rgb(50, 121, 62);">CAP campaign on the ISODEC site.</a></p><p style="margin-right: 0px; margin-bottom: 1em; margin-left: 0px; padding: 0px; font-size: 0.9em;"><br></p></div></div></div></div></div></div></div></div></div>', '6238605712c3a.jpg', NULL, '2002-05-28 00:00:00', NULL, 1, NULL, 19);
INSERT INTO "public"."contents" VALUES ('COST BENCHMARKING & REAL-TIME TRAINING WORKSHOP', 2, '9602544c-184c-4c7a-9ea9-d3297e57efbb', 'COST BENCHMARKING & REAL-TIME TRAINING WORKSHOP', '<p><b>BENCHMARKING?</b></p><p>•</p><p>T<font color="#0000ff">he benchmarking process involves comparing firms’ performance on a set of measurable parameters of strategic importance against that of firms’ known to have achieved best performance on those indicators.</font></p><p><font color="#0000ff">•</font></p><p><font color="#0000ff">Benchmarking focuses on best practices rather than merely measuring the best performance.</font></p><p><br></p><p><br></p><p>Download for more...</p>', '0617e8ddcc0e951.17855228.jpg', NULL, '2017-11-14 00:00:00', NULL, NULL, NULL, 27);
